create table manutencoes (id_manutencao bigint not null auto_increment, ds_motivo_manutencao varchar(255), ds_observacao text, dt_agendamento datetime(6), dt_entrada datetime(6), dt_fim datetime(6), status_manutencao varchar(15) not null, tp_manutencao varchar(15) not null, tp_status varchar(255), vl_manutencao float(53), fornecedor_id bigint not null, patrimonio_id bigint not null, primary key (id_manutencao));